     1â†’import { NextRequest, NextResponse } from 'next/server';
     2â†’import { prisma } from '@/lib/db';
     3â†’import { z } from 'zod';
     4â†’
     5â†’const ComponentType = z.enum([
     6â†’  'MCP_SERVER',
     7â†’  'SUBAGENT',
     8â†’  'SKILL',
     9â†’  'COMMAND',
    10â†’  'HOOK',
    11â†’  'CLAUDE_MD_TEMPLATE',
    12â†’]);
    13â†’
    14â†’const CreateComponentSchema = z.object({
    15â†’  type: ComponentType,
    16â†’  name: z.string().min(1).max(100),
    17â†’  description: z.string(),
    18â†’  config: z.record(z.unknown()),
    19â†’  sourceUrl: z.string().url().optional().nullable(),
    20â†’  version: z.string().optional().nullable(),
    21â†’  tags: z.string().default(''),
    22â†’  enabled: z.boolean().default(true),
    23â†’});
    24â†’
    25â†’export async function GET(request: NextRequest) {
    26â†’  try {
    27â†’    const { searchParams } = new URL(request.url);
    28â†’    const type = searchParams.get('type');
    29â†’    const search = searchParams.get('search');
    30â†’    const tags = searchParams.get('tags');
    31â†’
    32â†’    const where: Record<string, unknown> = {};
    33â†’
    34â†’    if (type) {
    35â†’      where.type = type;
    36â†’    }
    37â†’
    38â†’    if (search) {
    39â†’      where.OR = [
    40â†’        { name: { contains: search } },
    41â†’        { description: { contains: search } },
    42â†’      ];
    43â†’    }
    44â†’
    45â†’    if (tags) {
    46â†’      const tagList = tags.split(',').map((t) => t.trim());
    47â†’      where.AND = tagList.map((tag) => ({
    48â†’        tags: { contains: tag },
    49â†’      }));
    50â†’    }
    51â†’
    52â†’    const components = await prisma.component.findMany({
    53â†’      where,
    54â†’      orderBy: [{ type: 'asc' }, { name: 'asc' }],
    55â†’    });
    56â†’
    57â†’    // Parse config JSON for each component
    58â†’    const parsed = components.map((c) => ({
    59â†’      ...c,
    60â†’      config: JSON.parse(c.config),
    61â†’    }));
    62â†’
    63â†’    return NextResponse.json({
    64â†’      components: parsed,
    65â†’      total: parsed.length,
    66â†’    });
    67â†’  } catch (error) {
    68â†’    console.error('GET /api/components error:', error);
    69â†’    return NextResponse.json(
    70â†’      { error: 'Failed to fetch components' },
    71â†’      { status: 500 }
    72â†’    );
    73â†’  }
    74â†’}
    75â†’
    76â†’export async function POST(request: NextRequest) {
    77â†’  try {
    78â†’    const body = await request.json();
    79â†’    const validated = CreateComponentSchema.parse(body);
    80â†’
    81â†’    const component = await prisma.component.create({
    82â†’      data: {
    83â†’        type: validated.type,
    84â†’        name: validated.name,
    85â†’        description: validated.description,
    86â†’        config: JSON.stringify(validated.config),
    87â†’        sourceUrl: validated.sourceUrl ?? null,
    88â†’        version: validated.version ?? null,
    89â†’        tags: validated.tags,
    90â†’        enabled: validated.enabled,
    91â†’      },
    92â†’    });
    93â†’
    94â†’    return NextResponse.json(
    95â†’      {
    96â†’        ...component,
    97â†’        config: JSON.parse(component.config),
    98â†’      },
    99â†’      { status: 201 }
   100â†’    );
   101â†’  } catch (error) {
   102â†’    if (error instanceof z.ZodError) {
   103â†’      return NextResponse.json(
   104â†’        { error: 'Validation failed', details: error.errors },
   105â†’        { status: 400 }
   106â†’      );
   107â†’    }
   108â†’
   109â†’    if (
   110â†’      error instanceof Error &&
   111â†’      error.message.includes('Unique constraint')
   112â†’    ) {
   113â†’      return NextResponse.json(
   114â†’        { error: 'Component with this type and name already exists' },
   115â†’        { status: 409 }
   116â†’      );
   117â†’    }
   118â†’
   119â†’    console.error('POST /api/components error:', error);
   120â†’    return NextResponse.json(
   121â†’      { error: 'Failed to create component' },
   122â†’      { status: 500 }
   123â†’    );
   124â†’  }
   125â†’}
   126â†’

