    46â†’/**
    47â†’ * Helper function to validate task metadata configuration
    48â†’ */
    49â†’export function validateTaskMetadata(metadataContent: string): { valid: boolean; errors: string[] } {
    50â†’  const errors: string[] = [];
    51â†’
    52â†’  try {
    53â†’    const metadata = JSON.parse(metadataContent) as TaskMetadata;
    54â†’
    55â†’    // Validate structure
    56â†’    if (!metadata.models || !metadata.thinking) {
    57â†’      errors.push('Task metadata must include both models and thinking configurations');
    58â†’      return { valid: false, errors };
    59â†’    }
    60â†’
    61â†’    const phases = ['spec', 'planning', 'coding', 'qa'];
    62â†’    const validModels = ['opus', 'sonnet', 'haiku'];
    63â†’    const validThinkingLevels = ['none', 'low', 'medium', 'high', 'ultrathink'];
    64â†’
    65â†’    // Validate models for each phase
    66â†’    for (const phase of phases) {
    67â†’      if (!metadata.models[phase as keyof typeof metadata.models]) {
    68â†’        errors.push(`Missing model configuration for ${phase} phase`);
    69â†’      } else {
    70â†’        const model = metadata.models[phase as keyof typeof metadata.models];
    71â†’        if (!validModels.includes(model)) {
    72â†’          errors.push(`Invalid model '${model}' for ${phase} phase. Must be one of: ${validModels.join(', ')}`);
    73â†’        }
    74â†’      }
    75â†’
    76â†’      if (!metadata.thinking[phase as keyof typeof metadata.thinking]) {
    77â†’        errors.push(`Missing thinking configuration for ${phase} phase`);
    78â†’      } else {
    79â†’        const thinking = metadata.thinking[phase as keyof typeof metadata.thinking];
    80â†’        if (!validThinkingLevels.includes(thinking)) {
    81â†’          errors.push(`Invalid thinking level '${thinking}' for ${phase} phase. Must be one of: ${validThinkingLevels.join(', ')}`);
    82â†’        }
    83â†’      }
    84â†’    }
    85â†’
    86â†’  } catch (parseError) {
    87â†’    errors.push('Invalid JSON format in task metadata');
    88â†’  }
    89â†’
    90â†’  return {
    91â†’    valid: errors.length === 0,
    92â†’    errors
    93â†’  };
    94â†’}
