   137â†’/**
   138â†’ * Helper function to validate environment variables for Auto-Claude
   139â†’ */
   140â†’export function validateAutoClaudeEnv(envContent: string): { valid: boolean; errors: string[] } {
   141â†’  const errors: string[] = [];
   142â†’  const lines = envContent.split('\n');
   143â†’  const envVars: Record<string, string> = {};
   144â†’
   145â†’  // Parse env variables
   146â†’  for (const line of lines) {
   147â†’    const trimmed = line.trim();
   148â†’    if (trimmed && !trimmed.startsWith('#')) {
   149â†’      const [key, ...valueParts] = trimmed.split('=');
   150â†’      if (key && valueParts.length > 0) {
   151â†’        envVars[key] = valueParts.join('=');
   152â†’      }
   153â†’    }
   154â†’  }
   155â†’
   156â†’  // Validate required variables
   157â†’  if (!envVars.ANTHROPIC_API_KEY || envVars.ANTHROPIC_API_KEY === '') {
   158â†’    errors.push('ANTHROPIC_API_KEY is required but not set');
   159â†’  }
   160â†’
   161â†’  if (envVars.AUTO_CLAUDE_ENABLED === 'true') {
   162â†’    // Check for at least one MCP server enabled
   163â†’    const mcpEnabled = [
   164â†’      envVars.CONTEXT7_ENABLED === 'true',
   165â†’      envVars.LINEAR_MCP_ENABLED === 'true',
   166â†’      envVars.ELECTRON_MCP_ENABLED === 'true',
   167â†’      envVars.PUPPETEER_MCP_ENABLED === 'true',
   168â†’      envVars.GRAPHITI_ENABLED === 'true'
   169â†’    ].some(Boolean);
   170â†’
   171â†’    if (!mcpEnabled) {
   172â†’      errors.push('At least one MCP server should be enabled when AUTO_CLAUDE_ENABLED is true');
   173â†’    }
   174â†’
   175â†’    // Validate Linear integration if enabled
   176â†’    if (envVars.LINEAR_MCP_ENABLED === 'true' && !envVars.LINEAR_API_KEY) {
   177â†’      errors.push('LINEAR_API_KEY is required when LINEAR_MCP_ENABLED is true');
   178â†’    }
   179â†’
   180â†’    // Validate GitHub integration if GitHub token is present
   181â†’    if (envVars.GITHUB_TOKEN && !envVars.GITHUB_REPO) {
   182â†’      errors.push('GITHUB_REPO should be specified when GITHUB_TOKEN is present');
   183â†’    }
   184â†’
   185â†’    // Validate Graphiti integration if enabled
   186â†’    if (envVars.GRAPHITI_ENABLED === 'true' && !envVars.GRAPHITI_API_KEY) {
   187â†’      errors.push('GRAPHITI_API_KEY is required when GRAPHITI_ENABLED is true');
   188â†’    }
   189â†’  }
   190â†’
   191â†’  return {
   192â†’    valid: errors.length === 0,
   193â†’    errors
   194â†’  };
   195â†’}
