    17â†’/**
    18â†’ * Generates prompts/*.md files with injection point replacement
    19â†’ */
    20â†’export function generateAutoClaudePrompts(options: PromptsOptions): GeneratedPromptFile[] {
    21â†’  const { prompts, injectionContext = {} } = options;
    22â†’  const generatedFiles: GeneratedPromptFile[] = [];
    23â†’
    24â†’  for (const prompt of prompts) {
    25â†’    const content = processPromptContent(prompt, injectionContext);
    26â†’
    27â†’    generatedFiles.push({
    28â†’      path: `prompts/${prompt.agentType}.md`,
    29â†’      content
