   205â†’/**
   206â†’ * Helper function to get default injection context
   207â†’ */
   208â†’export function getDefaultInjectionContext(): NonNullable<PromptsOptions['injectionContext']> {
   209â†’  return {
   210â†’    specDirectory: './.auto-claude/specs/current',
   211â†’    projectContext: '# Project Analysis\n\n*Project context will be generated during execution*',
   212â†’    mcpDocumentation: '# MCP Server Documentation\n\n*MCP documentation will be loaded based on enabled servers*'
   213â†’  };
   214â†’}
   215â†’
   216â†’/**
   217â†’ * Helper function to extract injection points from prompt content
   218â†’ */
   219â†’export function extractInjectionPoints(promptContent: string): string[] {
   220â†’  const injectionPointPattern = /\{\{([^}]+)\}\}/g;
   221â†’  const points: string[] = [];
   222â†’  let match;
   223â†’
   224â†’  while ((match = injectionPointPattern.exec(promptContent)) !== null) {
   225â†’    if (!points.includes(match[1])) {
   226â†’      points.push(match[1]);
   227â†’    }
   228â†’  }
   229â†’
   230â†’  return points;
   231â†’}

