     3â†’interface PromptsOptions {
     4â†’  prompts: AutoClaudePrompt[];
     5â†’  injectionContext?: {
     6â†’    specDirectory?: string;
     7â†’    projectContext?: string;
     8â†’    mcpDocumentation?: string;
     9â†’  };
    10â†’}
    11â†’
    12â†’interface GeneratedPromptFile {
    13â†’  path: string;
    14â†’  content: string;
    15â†’}
    16â†’
    17â†’/**
    18â†’ * Generates prompts/*.md files with injection point replacement
    19â†’ */
    20â†’export function generateAutoClaudePrompts(options: PromptsOptions): GeneratedPromptFile[] {
    21â†’  const { prompts, injectionContext = {} } = options;
    22â†’  const generatedFiles: GeneratedPromptFile[] = [];
    23â†’
    24â†’  for (const prompt of prompts) {
    25â†’    const content = processPromptContent(prompt, injectionContext);
    26â†’
    27â†’    generatedFiles.push({
    28â†’      path: `prompts/${prompt.agentType}.md`,
    29â†’      content
    30â†’    });
    31â†’  }
    32â†’
    33â†’  return generatedFiles;
    34â†’}
    35â†’
    36â†’/**
    37â†’ * Processes prompt content by replacing injection points with actual values
    38â†’ */
    39â†’function processPromptContent(
    40â†’  prompt: AutoClaudePrompt,
    41â†’  injectionContext: PromptsOptions['injectionContext'] = {}
    42â†’): string {
    43â†’  let content = prompt.promptContent;
    44â†’
    45â†’  // Replace injection points if they exist in the prompt content
    46â†’  if (prompt.injectionPoints) {
    47â†’    // Replace spec directory injection point
    48â†’    if (prompt.injectionPoints.specDirectory && injectionContext.specDirectory) {
    49â†’      content = content.replace(
    50â†’        /\{\{specDirectory\}\}/g,
    51â†’        injectionContext.specDirectory
    52â†’      );
    53â†’    }
    54â†’
    55â†’    // Replace project context injection point
    56â†’    if (prompt.injectionPoints.projectContext && injectionContext.projectContext) {
    57â†’      content = content.replace(
    58â†’        /\{\{projectContext\}\}/g,
    59â†’        injectionContext.projectContext
    60â†’      );
    61â†’    }
    62â†’
    63â†’    // Replace MCP documentation injection point
    64â†’    if (prompt.injectionPoints.mcpDocumentation && injectionContext.mcpDocumentation) {
    65â†’      content = content.replace(
    66â†’        /\{\{mcpDocumentation\}\}/g,
    67â†’        injectionContext.mcpDocumentation
    68â†’      );
    69â†’    }
    70â†’  }
    71â†’
    72â†’  // Remove any remaining unresolved injection points
    73â†’  content = content.replace(/\{\{[^}]+\}\}/g, '<!-- Injection point not resolved -->');
    74â†’
    75â†’  // Ensure proper markdown formatting with header comment
    76â†’  const lines = content.split('\n');
    77â†’  const hasGeneratedComment = lines.some(line =>
    78â†’    line.includes('Generated by Claude Code Config Manager')
    79â†’  );
    80â†’
    81â†’  if (!hasGeneratedComment) {
    82â†’    const headerComment = [
    83â†’      '<!-- Generated by Claude Code Config Manager -->',
    84â†’      '<!-- Do not edit manually - sync from CCM to update -->',
    85â†’      ''
    86â†’    ];
    87â†’
    88â†’    // Insert after any existing front matter or at the beginning
    89â†’    const frontMatterEnd = findFrontMatterEnd(lines);
    90â†’    lines.splice(frontMatterEnd, 0, ...headerComment);
    91â†’  }
    92â†’
    93â†’  return lines.join('\n');
    94â†’}
    95â†’
    96â†’/**
    97â†’ * Finds the end of YAML front matter in markdown content
    98â†’ */
    99â†’function findFrontMatterEnd(lines: string[]): number {
   100â†’  if (lines.length === 0 || lines[0].trim() !== '---') {
   101â†’    return 0;
   102â†’  }
   103â†’
   104â†’  for (let i = 1; i < lines.length; i++) {
   105â†’    if (lines[i].trim() === '---') {
   106â†’      return i + 1;
   107â†’    }
   108â†’  }
   109â†’
   110â†’  // If no closing --- found, assume no front matter
   111â†’  return 0;
   112â†’}
