   114â†’/**
   115â†’ * Helper function to validate prompt content and injection points
   116â†’ */
   117â†’export function validatePromptContent(prompt: AutoClaudePrompt): { valid: boolean; errors: string[] } {
   118â†’  const errors: string[] = [];
   119â†’
   120â†’  // Validate required fields
   121â†’  if (!prompt.agentType || prompt.agentType.trim() === '') {
   122â†’    errors.push('Agent type is required');
   123â†’  }
   124â†’
   125â†’  if (!prompt.promptContent || prompt.promptContent.trim() === '') {
   126â†’    errors.push('Prompt content is required');
   127â†’  }
   128â†’
   129â†’  // Validate agent type format (alphanumeric with underscores)
   130â†’  if (prompt.agentType && !/^[a-zA-Z_][a-zA-Z0-9_]*$/.test(prompt.agentType)) {
   131â†’    errors.push('Agent type must be alphanumeric with underscores only');
   132â†’  }
   133â†’
   134â†’  // Check for injection point placeholders in content
   135â†’  const injectionPointPattern = /\{\{([^}]+)\}\}/g;
   136â†’  const foundInjectionPoints = new Set<string>();
   137â†’  let match;
   138â†’
   139â†’  while ((match = injectionPointPattern.exec(prompt.promptContent)) !== null) {
   140â†’    foundInjectionPoints.add(match[1]);
   141â†’  }
   142â†’
   143â†’  // Validate that declared injection points match those found in content
   144â†’  if (prompt.injectionPoints) {
   145â†’    const declaredPoints = new Set<string>();
   146â†’
   147â†’    if (prompt.injectionPoints.specDirectory) {
   148â†’      declaredPoints.add('specDirectory');
   149â†’    }
   150â†’    if (prompt.injectionPoints.projectContext) {
   151â†’      declaredPoints.add('projectContext');
   152â†’    }
   153â†’    if (prompt.injectionPoints.mcpDocumentation) {
   154â†’      declaredPoints.add('mcpDocumentation');
   155â†’    }
   156â†’
   157â†’    // Check for undeclared injection points in content
   158â†’    for (const foundPoint of foundInjectionPoints) {
