   181â†’      const value = valueParts.join('=').replace(/^["']|["']$/g, ''); // Remove quotes
   182â†’
   183â†’      switch (key.trim()) {
   184â†’        case 'CONTEXT7_ENABLED':
   185â†’          config.context7Enabled = value.toLowerCase() === 'true';
